mapper.select:
select

from gas_bottle_manager
where 1=1

  and cylinder_barcode LIKE concat(concat('%',#{cylinderBarcode}),'%')


  AND manufacturing_unit like concat(concat('%',#{manufacturingUnit}),'%')


  AND cylinder_barcode LIke concat(concat('%',#{cylinderBarcode}),'%')


  AND landing_date >= #{beginLandingDate}


  AND landing_date <= #{endLandingDate}


  AND last_inspection_date >= #{beginLastInspectionDate}


  AND last_inspection_date <= #{endLastInspectionDate}


  AND next_inspection_date >= #{beginNextInspectionDate}


  AND next_inspection_date <= #{endNextInspectionDate}
